                                            @endif
                                        </div>

                                        <div class="add-btn">
                                            <label for="attachment_sptData" style="cursor: pointer;">Add</label>
                                            <input type="file" id="attachment_sptData" name="attachment_spt[]"
                                                oninput="addMultipleFiles(this, 'attachment_sptfield')" multiple hidden>
